import React, { createContext, useState } from 'react';
import { ReactNode } from 'react';

type Expense = {
id: number;
user_id: number;
amount: string;
expense_type_id: number;
date: string;
}

type ExpensesContextType = {
    expenses: Expense[]; 
    setExpenses: React.Dispatch<React.SetStateAction<Expense[]>>; // Same here for the type
  };

  type ExpensesContextProviderProps = {
    children: ReactNode;
  };

  // Create the context with the default value
  export const ExpenseContext = createContext<ExpensesContextType | null>(null);

  export const ExpensesContextProvider: React.FC<ExpensesContextProviderProps> = ({ children }) => {
    const [expenses, setExpenses] = useState<Expense[]>([]);


      return (
          <ExpensesContext.Provider value={{ expenses, setExpenses }}>
              {children}
          </ExpensesContext.Provider>
      );
  }
