import React, { useEffect, useContext } from 'react'
import Header from '../components/Header'
import Box from '@mui/material/Box';
import CoffeeIcon from '@mui/icons-material/Coffee';
import FastfoodIcon from '@mui/icons-material/Fastfood';
import LocalBarIcon from '@mui/icons-material/LocalBar';
import ExpenseFinder from '../apis/ExpenseFinder';
import { ExpensesContext } from '../context/ExpensesContext';
import { Typography } from '@mui/material';



const Home: React.FC = () => {
  const context = useContext(ExpensesContext);

  if (!context) {
    throw new Error('ExpensesContext.Provider is missing around this component');
  }
  const { expenses, setExpenses } = context;
  const userId = localStorage.getItem('userId');

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await ExpenseFinder.get(`/users/${userId}`);
        console.log(response.data);
        setExpenses(response.data);

      } catch (error) {
        console.error(error);
      }
    };
    fetchData();
  }, []);

  return (
    <Box m={2}>
      <Header text='Am I spending too much?' />
      {expenses.map((expense) => (
        <Box key={expense.expense_type_id} display="flex" justifyContent="space-between" alignItems="center">
          <Box display="flex" alignItems="center">
            {/* {getIcon(expense.expense_type_id)} */}
            <Typography variant="body1" style={{ marginLeft: '8px' }}>
              {expense.expense_type_name}
            </Typography>
          </Box>
          <Typography variant="body1">
            ${expense.total_amount} / week
          </Typography>
          <Typography variant="body1" style={{ color: (expense.total_amount ?? 0) > parseFloat(expense.avg_amount) ? 'red' : 'green' }}>
            {((expense.total_amount ?? 0) > parseFloat(expense.avg_amount)) ? 'above' : 'below'} average
          </Typography>

        </Box>
      ))}
      {/* <Button variant="contained" color="primary" onClick={() => setIsEdit(!isEdit)}>
        {isEdit ? 'Edit expenses' : 'Add expenses'}
      </Button> */}
    </Box>
  )
}

export default Home
