import React, { useState } from 'react';
import Header from '../components/Header';
import Box from '@mui/material/Box';
import TextField from '@mui/material/TextField';
import Button from '@mui/material/Button';
import CoffeeIcon from '@mui/icons-material/Coffee';
import FastfoodIcon from '@mui/icons-material/Fastfood';
import LocalBarIcon from '@mui/icons-material/LocalBar';
import InputAdornment from '@mui/material/InputAdornment';
import { useNavigate } from 'react-router-dom';
import ExpenseFinder from '../apis/ExpenseFinder';

function AddExpense() {
  const [coffeeAmount, setCoffeeAmount] = useState('');
  const [foodAmount, setFoodAmount] = useState('');
  const [alcoholAmount, setAlcoholAmount] = useState('');

  const [error, setError] = useState({
    coffee: '',
    food: '',
    alcohol: '',
    form: ''
  });  

  const navigate = useNavigate();

  const handleSubmit = async (event: React.MouseEvent<HTMLButtonElement>) => {
    event.preventDefault();
    const userIds = [
      91235, 93430, 5963, 29926, 67471,
      36235, 8122, 85641, 88791, 39853
    ];
    const expenseTypeIds = {
      Food: 1,
      Coffee: 2,
      Alcohol: 3
    };
    const randomIndex = Math.floor(Math.random() * userIds.length);
    const randomUserId = userIds[randomIndex];
    try {
      const response = await ExpenseFinder.post('/', {
        expenses: [
          { type_id: expenseTypeIds.Food, amount: foodAmount },
          { type_id: expenseTypeIds.Coffee, amount: coffeeAmount },
          { type_id: expenseTypeIds.Alcohol, amount: alcoholAmount }
        ],
        user_id: randomUserId,
      });
      setCoffeeAmount('');
      setFoodAmount('');
      setAlcoholAmount('');
      // console.log(response);
    } catch (err) {
      console.error(err);
    }
  };

  return (
    <Box m={2} >
      <Header text='How much did I spend today?' />
      <Box component="form" sx={{ maxWidth: 400 }}>

        <Box my={2}>
          <TextField
            fullWidth
            id="outlined-controlled"
            label="Coffee"
            value={coffeeAmount}
            onChange={(event: React.ChangeEvent<HTMLInputElement>) => {
              setCoffeeAmount(event.target.value);
            }}
            InputProps={{
              startAdornment: (
                <InputAdornment position="start">
                  <CoffeeIcon />
                  $
                </InputAdornment>
              ),
            }}
          />
        </Box>
        <Box my={2}>
          <TextField
            fullWidth
            id="outlined-controlled"
            label="Food"
            value={foodAmount}
            onChange={(event: React.ChangeEvent<HTMLInputElement>) => {
              setFoodAmount(event.target.value);
            }}
            InputProps={{
              startAdornment: (
                <InputAdornment position="start">
                  <FastfoodIcon />
                  $
                </InputAdornment>
              ),
            }}
          />
        </Box>
        <Box my={2}>
          <TextField
            fullWidth
            id="outlined-controlled"
            label="Alcohol"
            value={alcoholAmount}
            onChange={(event: React.ChangeEvent<HTMLInputElement>) => {
              setAlcoholAmount(event.target.value);
            }}
            InputProps={{
              startAdornment: (
                <InputAdornment position="start">
                  <LocalBarIcon />
                  $
                </InputAdornment>
              ),
            }}
          />
        </Box>
        <Box my={2} display="flex" justifyContent="space-between">
          <Button variant="outlined" onClick={() => navigate('/')}>Back</Button>
          <Button color="primary" variant="contained" type='submit' onClick={handleSubmit}>Add expenses</Button>
        </Box>

      </Box>
    </Box>
  );
}

export default AddExpense;
